import { FormControl, FormGroup, Validators } from '@angular/forms';
import { FileAPI } from 'src/app/_class/file-api';
import { Publication } from 'src/app/_class/publication';
import * as i0 from "@angular/core";
import * as i1 from "@angular/router";
import * as i2 from "src/app/_services/publication.service";
import * as i3 from "src/app/_services/upload-s3.service";
import * as i4 from "@angular/forms";
export class UpdatePublicationComponent {
    constructor(router, route, publicationService, uploadS3Service) {
        this.router = router;
        this.route = route;
        this.publicationService = publicationService;
        this.uploadS3Service = uploadS3Service;
        this.publication = new Publication();
        this.files = [];
        this.myForm = new FormGroup({
            contenu: new FormControl('', [
                Validators.required,
                Validators.minLength(3),
            ]),
            file: new FormControl(),
        });
        this.cheminImage = 'https://testp12.s3.eu-west-3.amazonaws.com/';
        this.fileAPI = new FileAPI();
    }
    ngOnInit() {
        this.id = this.route.snapshot.params['id'];
        this.getPublicationById(this.id);
    }
    getPublicationById(id) {
        this.publicationService.getPublicationById(id).subscribe(data => {
            this.publication = data;
        });
    }
    onSubmit() {
        this.publicationService.updatePublication(this.id, this.publication).subscribe(data => {
            this.publication = data;
            this.goToPublicationPublic();
        }, error => console.log(error));
    }
    goToPublicationPublic() {
        this.router.navigate(['home']);
    }
    onFileChange(event) {
        for (var i = 0; i < event.target.files.length; i++) {
            this.files.push(event.target.files[i]);
        }
    }
    async newFileUpdate() {
        for (let i = 0; i < this.files.length; i += 1) {
            let file = this.files[i];
            let filePath = "image/" + file.name;
            //s3
            try {
                console.log("request upload file " + filePath);
                //s3
                let response = await this.uploadS3Service.uploadFileS3(file, filePath);
            }
            catch (error) {
                console.log("erreur lors de l'envoie de la publication");
            }
            //api
            this.fileAPI.name = file.name;
            this.fileAPI.url = this.cheminImage + filePath;
            console.log('fileAPI updated ' + this.fileAPI);
            this.publication.listFile.push(this.fileAPI);
        }
        this.files = [];
        this.createPubAPI(this.publication);
    }
    createPubAPI(publicationRequest) {
        //le contenu 
        console.log('publicationRequest', publicationRequest);
        this.publication.contenu = this.myForm.controls['contenu'].value;
        //l'user 
        this.currentUser = this.tokenStorage.getUser();
        this.publication.username = this.currentUser.username;
        console.log('publication ', this.publication);
        this.publicationService
            .createPublication(this.publication)
            .subscribe((data) => {
            console.log("data created " + data);
        });
        window.location.reload();
    }
}
